  function _findSchemaId(compId) {
    var result = compId,
        modelName = '';

    for (modelName in factory.system().components()) {
      if (typeof factory.system().components()[modelName][compId] !== 'undefined') {
        result = modelName;
        break;
      }
    }
    return result;
  }

  function _getSchemaId(name) {
    var result = '',
        id = '';

    for (id in factory.system().schemas()) {
