      length = libraries.children.length;
      for (i = 0; i < length; i++) {
        $(libraries.children[i]).removeClass('active');
      }

      // add current active
      $(this).addClass('active');
    }
  };

  for (i = 0; i < nbExtensionUninstalled; i++) {
    extension = this.require(extensionsUninstalled[i]._id);

    dom = document.getElementById('designer-dialog-extension-modal-uninstalled-extensions-' + extension.id());
    dom.addEventListener('click', callbackEvent);
  }

  // extensions events  
