function remove() { 
	var extension = this.data(),
	extensions = [],
	extensionToSave = {
	  extensions: []
	};

	if (extension && Object.keys(extension).length) {
  	// TODO put that in function
  	this.require('runtime').uninstall('ext_' + extension.source()._id);
  	this.require('menubar').render();
  	this.require('toolbar').render();

  	if (extension.isCore()) {
  	  extension.installed(false);
  	} else {
  	  extension.destroy();
  	}

