function push(comment, createHTML, createNode, logLevel, createPackage) {
  var config = {},
    gh = null,
    userName = '',
    repository = '',
    designer = null,
    system = {},
    repo1 = null,
    repo2 = null,
    repo3 = null;

  config = this.require('storage').get('system-designer-config');
  if (config && config.githubToken && config.githubRepository) {

    gh = new GitHub({
      token: atob(config.githubToken)
    });

    userName = config.githubRepository.split('/')[0];
    repository = config.githubRepository.split('/')[1];

    designer = this.require('designer');

    if (this.require('factory').system()) {
      system = this.require('db').collections().System.find({
        '_id': this.require('factory').system().id()
      })[0];

      system = JSON.parse(JSON.stringify(system));
      delete system.classInfo;

      repo1 = gh.getRepo(userName, repository);
      repo2 = gh.getRepo(userName, repository);
      repo3 = gh.getRepo(userName, repository);
      repo4 = gh.getRepo(userName, repository);

      repo1.writeFile('master', system.name + '.json', JSON.stringify(system), comment || 'updated with System Designer', {})
        .then(function (response) {
          var name = response.data.content.name.replace('.json', '');
          var js = '';
          var htmlSource = '';
          var jsSource = '';
          var packageSource = {};

          runtime.require('message').success('system ' + name + ' pushed on GitHub.');

          // HTML
          if (createHTML) {
            htmlSource = runtime.require('export-app-github.html').source();
            htmlSource = htmlSource
              .replace(/{{logLevel}}/g, logLevel)
              .replace(/{{name}}/g, runtime.require('factory').system().name());

            repo2.writeFile('master', runtime.require('factory').system().name() + '.html', htmlSource, 'created with System Designer', {})
              .then(function (response) {
                var name = response.data.content.name;
                runtime.require('message').success(name + ' created on GitHub.');
              })
              .catch(function (e) {
                runtime.require('message').danger('<strong>GitHub:</strong> ' + e.response.data.message + '.');
              });
          }

          // Node
          if (createNode) {
            jsSource = runtime.require('app-github.js').source();
            jsSource = jsSource
              .replace(/{{logLevel}}/g, logLevel)
              .replace(/{{description}}/g, runtime.require('factory').system().description().replace(/\n/g, '\n * '))
              .replace(/{{version}}/g, runtime.require('factory').system().version())
              .replace(/{{name}}/g, runtime.require('factory').system().name());

            repo3.writeFile('master', runtime.require('factory').system().name() + '.js', jsSource, 'created with System Designer', {})
              .then(function (response) {
                var name = response.data.content.name;
                runtime.require('message').success(name + ' created on GitHub.');
              })
              .catch(function (e) {
                runtime.require('message').danger('<strong>GitHub:</strong> ' + e.response.data.message + '.');
              });
          }

          // PACKAGE
          if (createPackage) {
            packageSource.name = runtime.require('factory').system().name();
            packageSource.version = runtime.require('factory').system().version();
            packageSource.description = runtime.require('factory').system().description();
            packageSource.main = './' + runtime.require('factory').system().name() + '.json';
            packageSource.repository = {};
            packageSource.repository.type = 'git';
            packageSource.repository.url = 'https://github.com/' + config.githubRepository;
            packageSource.dependencies = {};
            packageSource.dependencies['system-runtime'] = '^2.0.0-rc.2';

            repo4.writeFile('master', 'package.json', JSON.stringify(packageSource, null, '\t').toString(), 'created with System Designer', {})
              .then(function (response) {
                var name = response.data.content.name;
                runtime.require('message').success(name + ' created on GitHub.');
              })
              .catch(function (e) {
                runtime.require('message').danger('<strong>GitHub:</strong> ' + e.response.data.message + '.');
              });
          }
        })
        .catch(function (e) {
          runtime.require('message').danger('<strong>GitHub:</strong> ' + e.response.data.message + '.');
        });
    } else {
      runtime.require('message').warning('There is no system to push on GitHub.');
    }
  } else {
    runtime.require('message').warning('No configuration found for GitHub. Please update the configuration.');
  }
}

