function addBehavior(model, component, state) {
  var that = this,
      schemas = this.system().schemas(),
      models = this.system().models(),
      modelDef = null,
      factory = this,
      behaviors = this.system().behaviors(),
      message = this.require('message'),
      schemaId = '',
      modelId = '',
      methodDef = null,
      behavior = {},
      result = '',
      body = '\t\n',
      ModelBehavior = null,
      modelBehavior = null,
      uuid = '',
      params = '',
      canCreate = true,
      i = 0,
      length = 0;

  function _getSchemaId(name) {
    var result = '',
      id = '';

    for (id in that.system().schemas()) {
      if (that.system().schemas()[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _getModelId(name) {
    var result = '',
      id = '';

    for (id in that.system().models()) {
      if (that.system().models()[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _existBehavior(state, space, model) {
    var result = false;

    if (_isModel(space)) {
      for (id in that.system().behaviors()) {
        if (that.system().behaviors()[id].state === state && that.system().behaviors()[id].component === model) {
          result = true;
          break;
        }
      }
    } else {
      for (id in that.system().behaviors()) {
        if (that.system().behaviors()[id].state === state && that.system().behaviors()[id].component === space) {
          result = true;
          break;
        }
      }
    }
    return result;
  }

  function _isModel(name) {
    var result = false;

    if (Object.keys(that.system().components()).indexOf(name) !== -1) {
      result = true;
    }

    return result;
  }

  // get value

  if (model && state) {

    uuid = this.require('factory').generateId();

    if (model !== this.require('factory').system().name()) {

      schemaId = _getSchemaId(model);
      modelId = _getModelId(model);
      modelDef = this.getGeneratedModel(model);

      // params
      if (modelDef[state]) {
        methodDef = modelDef[state].params;
      }
      params = this.require('language').createBehaviorParameters(methodDef);

      if (schemas[schemaId][state] === 'property' || schemas[schemaId][state] === 'link') {
        params = 'value';
      }

      if (schemas[schemaId][state] === 'collection') {
        params = 'value, type';
      }

      if (schemas[schemaId][state] === 'method') {
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      if (state === 'init') {
        params = 'conf';
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      if (state === 'destroy') {
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      if (state === 'error') {
        params = 'data';
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      // body
      if (modelDef[state]) {
        result = modelDef[state].result;
      }
      if (result) {
       body = this.require('language').createBehaviorBody(result);
      }
    } else {
      component = this.require('factory').system().id();
      if (_existBehavior(state, component, model)) {
        canCreate = false;
      }
    }

    if (canCreate) {
      // set model
      if (state !== 'destroy') {
        behavior = {
          "_id": uuid,
          "component": component,
          "state": state,
          "action": this.require('language').createBehavior(state, params, body),
          "useCoreAPI": false,
          "core": false
        };
      } else {
        behavior = {
          "_id": uuid,
          "component": component,
          "state": "destroy",
          "action": this.require('language').createDestroyBehavior(),
          "useCoreAPI": true,
          "core": false
        };
      }

      behaviors[uuid] = behavior;
      this.require('factory').system().behaviors(behaviors);
    }
  }

  return JSON.parse(JSON.stringify(behavior));
}

