function context() { 
  var context = {},
      collection ='',
      id = '',
      property = '',
      url = '' ,
      path = [],
      i = 0,
      length = 0,
      apiName = this.require('restServer').apiName();

  url = this.request().url;  

  if (apiName === '') {
    this.require('logger').warn('API name not found, please set one.');
  } else {
    apiName = apiName + '/';
    apiName = apiName.replace('//', '/');

    if (url.indexOf(apiName) !== -1) {
      path = url.split(apiName)[1].split('/');
      length = path.length;

      for (i = 0; i < length; i++) {
        if (i === 0) {
          collection = path[i].split('?')[0];
        }
        if (i === 1) {
          id = path[i].split('?')[0];
        }
        if (i === 2) {
          property = path[i].split('?')[0];
        }
      }
    }
  }

  switch (this.request().method) {
    case 'GET':
      context.collection = collection;
      context.id = id;
      context.property = property;
      break;
    case 'DELETE':
      context.collection = collection;
      context.id = id;
      break;
    case 'POST':
      context.collection = collection;
      context.document = {};
      break;
    case 'PATCH':
      context.collection = collection;
      context.id = id;
      break;
    case 'PUT':
      context.collection = collection;
      context.id = id;
      break;
    default:
      break;
  }

  return context;
}

