function patch(collection, id, field, value) { 
  var restClient = this.require('restClient'),
      url = 'http://',
      systemId = this.require('runtime').system().id(),
      body = {};

  if (restClient.ssl()) {
    url = 'https://';
  }
  url = url + restClient.ip() + ':' + restClient.port() + '/' + restClient.apiName() + '/';
  url = url.replace(/\/\//g, '/');
  url = url + collection + '/' + id;

  body[field] = value;

  fetch(url, {
    method: 'PATCH',
    headers: new Headers({
	    'Content-Type': 'application/json',
	    'X-System-Id': systemId,
	    'X-System-Event': 'clientDatabaseUpdate'
    }),
    body: JSON.stringify(body)
  });
}

