function listen() { 
  var restClient = this.require('restClient'),
      url = 'http://',
      source = null;

  if (restClient.ssl()) {
    url = 'https://';
  }
  url = url + restClient.ip() + ':' + restClient.port() + '/stream';
  source = new EventSource(url);

  source.addEventListener('serverDatabaseInsert', function serverDatabaseInsert(event) {
    // a message has arrived from another system
    this.require('runtime').message(JSON.parse(event.data));
  }.bind(this));

  source.addEventListener('serverDatabaseRemove', function serverDatabaseRemove(event) {
    // a message has arrived from another system
    this.require('runtime').message(JSON.parse(event.data));
  }.bind(this));

  source.addEventListener('serverDatabaseUpdate', function serverDatabaseUpdate(event) {
    // a message has arrived from another system
    this.require('runtime').message(JSON.parse(event.data));
  }.bind(this));
}

