function send(message) { 

  // send the message to the server (which is another system)

  switch (message.event) {
    case 'clientGet':
      this.require('httpRequest').get(message.data[0].collection, message.data[0].id);
      break;
    case 'clientDatabaseInsert':
      this.require('httpRequest').post(message.data[0].collection, message.data[0].document);
      break;
    case 'clientDatabaseUpdate':
      this.require('httpRequest').patch(message.data[0].collection, message.data[0].id, message.data[0].field, message.data[0].value);
      break;
     case 'clientDatabaseRemove':
      this.require('httpRequest').del(message.data[0].collection, message.data[0].id);
      break;
   default:
      break;
  }
}

