function render() { 
  var model = this.model(),
      filter = [],
      result = [],
      props = {},
      html = '<table class="table">',
      i = 0,
      j = 0,
      logger = this.require('logger'),
      header = document.getElementById('header'),
      table = document.getElementById('table');

  if (model) {
    if (header && table) {

      header.innerHTML = model.toUpperCase();

      // table
      filter = this.require('util').createFilter(this.filter());
      result = this.require('db').collections()[model].find(filter);
      props = this.require(model).classInfo().properties();

      // header
      html = html + '<tr>';
      for (i=1; i<props.length; i++) {
        html = html + '<th scope="col">' + props[i] +'</th>'; 
      }
      html = html + '<tr>';

      // body
      for (i=0; i<result.length; i++) {
        html = html + '<tr>';
        for (j=1; j<props.length; j++) {
          html = html + '<td>' + result[i][props[j]] + '</td>';
        }
        html = html + '</tr>';
      }

      // end table
      html = html + '</table>';

      table.innerHTML = html;
    }
  } else {
    logger.warn('no model found for the list.');
  }
}

