function init(conf) { 
  var properties = [],
      modelName = this.model(),
      model = null;

  if (modelName) {
    model = this.require(modelName);

    // refresh the list when a property of the model changed
    model.classInfo().properties().forEach(function forEach(val) {
      this.on(val, function on(value) {
        this.require('list').render();
      });
    }.bind(model));

    // refresh the list when a component of the model is created
    model.on('init', function init (conf) {
      this.require('list').render();
    });

    // refresh the list when a component of the model is destroyed
    model.on('destroy', function destroy () {
      $component.destroy(this.id());
      $component.get('list').render();
    }, true);
  }
}

