function saveAs(data, fileName) {
    var textToWrite = null,
        textFileAsBlob = null,
        fileNameToSaveAs = null,
        downloadLink = null,
        message = this.require('message');

    textToWrite = JSON.stringify(data);
    fileNameToSaveAs = fileName;

    if (!this.isElectron()) {

      textFileAsBlob = new Blob([textToWrite], {
          type: 'text/plain'
      });

      if (navigator.msSaveBlob) {
        navigator.msSaveBlob(textFileAsBlob, fileNameToSaveAs);
      } else {
        downloadLink = document.createElement('a');
        downloadLink.download = fileNameToSaveAs;
        downloadLink.innerHTML = 'Download File';
        if (window.webkitURL != null) {
            downloadLink.href = window.webkitURL.createObjectURL(textFileAsBlob);
        } else {
            downloadLink.href = window.URL.createObjectURL(textFileAsBlob);
            downloadLink.onclick = function (event) {
                document.body.removeChild(event.target);
            };
            downloadLink.style.display = 'none';
            document.body.appendChild(downloadLink);
        }

        downloadLink.click();
      }

      message.success('Exportation is done.');
    } else {
      var {dialog} = require("electron").remote;
      var fs = require('fs');
      var savePath = dialog.showSaveDialog({
        'defaultPath': '~/' + fileNameToSaveAs
      });

      if (savePath) {
        fs.writeFile(savePath, textToWrite, function(err) {
          if (err) {
            message.danger('Exportation failed.');
          } else {
            message.success('Exportation is done.');
          }
        });
      }
    }
}

