function init(conf) { 
	var html = '',
      dom = null,
      extensions = [],
      extensionsUninstalled = [],
      extension = '',
      listExtensions = '',
      listExtensionsUninstalled ='',
      systems = null,
      sys = '',
      system = null,
      length = 0,
      i = 0,
      that = this,
      nbExtensionToAdd = 0,
      nbExtensionUninstalled = 0,
      nbExtensionInstalled = 0,
      isSystemContainsExtension = false;

  $('.modal-backdrop').remove();
  $('#designer-dialog').empty();

  // init extensions list
  extensions = this.require('db').collections().Extension.find({
    'installed': true
  });

  nbExtensionInstalled = extensions.length;
  for (i = 0; i < nbExtensionInstalled; i++) {
    extension = this.require(extensions[i]._id);

    listExtensions = listExtensions + '<a class="list-group-item" id="designer-dialog-extension-modal-extensions-' + extension.id() + '">' +
      '<h4 class="list-group-item-heading">' + extension.source().description.substr(0, 40).split('\n')[0].split('.')[0] + '</h4>' +
      '<p class="list-group-item-text">v' + extension.source().version + '</p>' +
      '</a>';
  }
  if (nbExtensionInstalled === 0) {
    listExtensions = '<a class="list-group-item"><h4 class="list-group-item-heading">No extension found</h4></a>';
  }

  // init extensions uninstalled list
  extensionsUninstalled = this.require('db').collections().Extension.find({
    'isCore': true
  });

  nbExtensionUninstalled = extensionsUninstalled.length;
  for (i = 0; i < nbExtensionUninstalled; i++) {
    extension = this.require(extensionsUninstalled[i]._id);

    listExtensionsUninstalled = listExtensionsUninstalled + '<a class="list-group-item" id="designer-dialog-extension-modal-uninstalled-extensions-' + extension.id() + '">' +
      '<h4 class="list-group-item-heading">' + extension.source().description.substr(0, 40).split('\n')[0].split('.')[0] + '</h4>' +
      '<p class="list-group-item-text">v' + extension.source().version + '</p>' +
      '</a>';
  }
  if (nbExtensionUninstalled === 0) {
    listExtensionsUninstalled = '<a class="list-group-item"><h4 class="list-group-item-heading">No extension found</h4></a>';
  }

  // init systems list
  systems = this.require('storage').get('system-designer-systems');
  if (systems) {
    systemIds = systems.systems;
    nbExtensionToAdd = systemIds.length;
  }

  for (i = 0; i < nbExtensionToAdd; i++) {
    system = this.require('storage').get(systemIds[i]);

    if (system && system.name.indexOf('extension-') !== -1) {
      isSystemContainsExtension = true;
      sys = sys + '<a class="list-group-item" id="designer-dialog-extension-modal-systems-' + system._id + '">' +
          '<h4 class="list-group-item-heading">' + system.description.substr(0, 40).split('\n')[0].split('.')[0] + '</h4>' +
          '<p class="list-group-item-text">v' + system.version + '</p>' +
          '</a>';
    }
  }

  if (nbExtensionToAdd === 0) {
    sys = '<a class="list-group-item"><h4 class="list-group-item-heading">No extension found</h4></a>';
  }

  html = this.require('dialog-modal-extension.html');
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',
    html.source()
     .replace(/{{title}}/gi, this.title())
        .replace(/{{extensions}}/gi, listExtensions)
        .replace(/{{uninstalled_extensions}}/gi, listExtensionsUninstalled)
        .replace(/{{systems}}/gi, sys)
  );

  // systems events  
  var callbackSystemEvent = function cbSystemEvent(event) {
    var id = '',
        libraries = null,
        length = 0,
        i = 0;

    if ($(this).hasClass('active')) {
      $(this).removeClass('active');
      that.data(null);
