      length = libraries.children.length;
      for (i = 0; i < length; i++) {
        $(libraries.children[i]).removeClass('active');
      }

      libraries = document.getElementById('designer-dialog-extension-modal-uninstalled-extensions');
      length = libraries.children.length;
