function initValue(value, lineNumber) {
  var that = this;

  switch (this.type()) {
    case 'ace':
      switch (this.context()) {
        case 'system':
        case 'schema':
        case 'model':
        case 'type':
          this.editor().setValue(value);
          this.editor().gotoLine(lineNumber);
          this.editor().getSession().$undoManager.reset();
          this.editor().getSession().setUndoManager(new ace.UndoManager());
          break;
        case 'behavior':
          this.editor().setValue(value);
          this.editor().gotoLine(lineNumber);
          this.editor().getSession().$undoManager.reset();
          this.editor().getSession().setUndoManager(new ace.UndoManager());

          var Range = ace.require('ace/range').Range;
          var endLine = value.indexOf('{') + 1;

          this.editor().session.addMarker(new Range(0, 0, 0, endLine), 'readonly');

          // readonly
          this.editor().keyBinding.addKeyboardHandler({
            handleKeyboard: function (data, hash, keyString, keyCode, event) {
              var result = null;

              switch (true) {
                case (hash === -1 || (keyCode <= 40 && keyCode >= 37)):
                  result = false;
                  break;
                case intersects(new Range(0, 0, 0, endLine)):
                  if (event.keyCode !== 67) {
                    result = {
                      command: 'null',
                      passEvent: false
                    };

                    if (event.keyCode !== 91 && event.keyCode !== 17) {
                      runtime.require('message').warning('You can not modify the header of the behavior.');
                    }
                  } else {
                    result = false;
                  }
                  break;
                default:
                  break;
              }

              if (result) {
                return result;
              }
            }
          });

          function intersects(range) {
            var result = false,
              selection = that.editor().getSelectionRange();

            result = (selection.end.row === 0 && selection.end.column < endLine + 1) && selection.intersects(range) && that.require('state').menu() === 'action';
            return result;
          }
          break;
        case 'component':
          break;
        default:
          break;
      }
      break;
    case 'codemirror':
      switch (this.context()) {
        case 'system':
        case 'schema':
        case 'model':
        case 'type':
        case 'behavior':
        case 'component':
          this.editor().setValue(value);
          this.editor().setCursor({
            line: lineNumber - 1,
            ch: 0
          });
          this.editor().clearHistory();
          break;
        default:
          break;
      }
      break;
    default:
      break;
  }
}

