function merge(sys) {
  var System = this.require('System'),
    name = '',
    propName = '',
    modelName = '',
    compId = '',
    modelId = '',
    schemaId = '',
    designer = this.require('designer'),
    schemas = {},
    models = {},
    types = {},
    components = {},
    behaviors = {},
    system = this.require('storage').get(sys._id),
    message = this.require('message');

  function _getModelId(name) {
    var result = '',
      id = '';

    for (id in system.models) {
      if (system.models[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _getSchemaId(name) {
    var result = '',
      id = '';

    for (id in system.schemas) {
      if (system.schemas[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _canOverrideBehavior(id, component, state, behaviors) {
    var behavior = {},
      behaviorId = '';

    for (behaviorId in behaviors) {
      behavior = behaviors[behaviorId];
      if (behavior.component === component && behavior.state === state && behavior._id !== id) {
        delete behaviors[behavior._id];
        break;
      }
    }
  }

  function _canOverride(sysId, schemas, behaviors, behavior) {
    var def = {},
      id = '',
      result = true;

    id = _getSchemaId(behavior.component);
    def = schemas[id];

    if (def && def[behavior.state]) {
      if (def[behavior.state] === 'method') {
        _canOverrideBehavior(behavior._id, behavior.component, behavior.state, behaviors);
      }
    }/* else {
      result = false;
    }*/ // case of behavior on components

    return result;
  }

  if (Object.keys(sys).length) {

    // schemas
    schemas = JSON.parse(JSON.stringify(system.schemas));
    for (name in sys.schemas) {
      if (schemas[name]) {
        for (propName in sys.schemas[name]) {
          schemas[name][propName] = sys.schemas[name][propName];
        }
      } else {
        if (!_getSchemaId(sys.schemas[name]._name)) {
          schemas[name] = sys.schemas[name];
        } else {
          for (propName in sys.schemas[name]) {
            if (propName.indexOf('_') !== 0) {
              schemas[_getSchemaId(sys.schemas[name]._name)][propName] = sys.schemas[name][propName];
            }
          }
        }
      }
    }
    system.schemas = schemas;

    // models
    models = JSON.parse(JSON.stringify(system.models));
    for (name in sys.models) {
      if (models[name]) {
        for (propName in sys.models[name]) {
          models[name][propName] = sys.models[name][propName];
        }
      } else {
        if (!_getModelId(sys.models[name]._name)) {
          models[name] = sys.models[name];
        } else {
          for (propName in sys.models[name]) {
            if (propName.indexOf('_') !== 0) {
              models[_getModelId(sys.models[name]._name)][propName] = sys.models[name][propName];
            }
          }
        }
      }
    }
    system.models = models;

    // types
    types = JSON.parse(JSON.stringify(system.types));
    for (name in sys.types) {
      if (types[name]) {
        for (propName in sys.types[name]) {
          types[name][propName] = sys.types[name][propName];
        }
      } else {
        types[name] = sys.types[name];
      }
    }
    system.types = types;

    // behaviors
    behaviors = JSON.parse(JSON.stringify(system.behaviors));
    for (name in sys.behaviors) {
      if (sys.behaviors[name].component === sys._id || _canOverride(sys._id, schemas, behaviors, sys.behaviors[name])) {
        behaviors[name] = sys.behaviors[name];
      }
    }
    system.behaviors = behaviors;

    // components
    components = JSON.parse(JSON.stringify(system.components));
    for (modelName in sys.components) {
      if (!components[modelName]) {
        components[modelName] = {};
      }
      for (compId in sys.components[modelName]) {
        if (components[modelName][compId]) {
          for (propName in sys.components[modelName][compId]) {
            components[modelName][compId][propName] = sys.components[modelName][compId][propName];
          }
        } else {
          components[modelName][compId] = sys.components[modelName][compId];
        }
      }
    }

    system.schemas = schemas;
    system.models = models;
    system.types = types;
    system.behaviors = behaviors;
    system.components = components;

    system.version = sys.version;
    system.description = sys.description;

    this.require('storage').set(sys._id, system);
    if (this.require('factory').system().id() === system._id) {
      this.require('factory').create(system);
    }
  } else {
    if (this.require('factory').system()) {
      message.danger('The system you try to merge is invalid.');
    } else {
      message.danger('There is no system to compose with.');
    }
  }
}

