function render() { 
  var html = null,
      that = this,
      doc = '',
      propName = '',
      propVal = '';

  // html 
  html = this.require('model-system.html');

  for (propName in this.document()) {
    if (['name', 'description', 'version'].indexOf(propName) !== -1) {
      propVal = this.document()[propName];
      propVal = propVal.replace(/\n/g, '<br>');
      doc = doc + '<tr><td>' + propName + '</td><td>' + propVal + '</td></tr>';
    }
  }

  document.querySelector('#designer-workspace').insertAdjacentHTML('afterbegin',
    html.source()
        .replace(/{{title}}/gi, this.title())
        .replace(/{{_id}}/gi, this.uuid())
        .replace(/{{content}}/gi, doc)
  );

  // events
  html = document.getElementById('designer-system-' + this.uuid()).children[0].children[1];
  if (html) {
    html.addEventListener('click', function click(event) {
      this.require('designer').open('system.html#' + that.uuid() + '#description', that.uuid());
    }.bind(this));
  }

  html = document.getElementById('designer-system-' + this.uuid() + '-delete');
  if (html) {
    html.addEventListener('click', function click(event) {
      var Dialog = this.require('DialogDelete'),
          data = {},
          dialog = {};

      dialog = new Dialog({
        'title': 'Confirm deletion',
        'message': 'Do you want to delete the system?<br><br>The related schemas, models, behaviors and components will be also deleted.'
      });

      data = dialog.data();

      data.ui = this.id();
      data.uuid = this.uuid();

      dialog.data(data);

      dialog.on('ok', function ok() {
        var systems = this.require('storage').get('system-designer-systems'),
            designer = this.require('designer'),
            state = this.require('state'),
            System = this.require('System'),
            systemId = this.data().uuid,
            that = this,
            isUninstalled = false;

        function _isExtensionInstalled (id) {
          var extensions = that.require('db').collections().Extension.find({
            'installed':true
          }),
              result = false;

          extensions.forEach(function (val) {
            if (val.source._id === id) {
              result = true;
            }
          });

          return result;
        }    

        // uninstall extension
        if (this.require('factory').system().name().indexOf('extension-') !== -1) {
          // check if installed as Extensions
          if (!_isExtensionInstalled(this.require('factory').system().id())) {
            if (this.require('ext_' + this.require('factory').system().id())) {
        	    this.require('runtime').uninstall('ext_' + this.require('factory').system().id());
        	    this.require('menubar').render();
        	    this.require('toolbar').render();

        	    isUninstalled = true;
            }
          }
        }    

        // remove from storage
        this.require('storage').remove(systemId);
        systems.systems.splice(systems.systems.indexOf(systemId), 1);
        this.require('storage').set('system-designer-systems', systems);

        this.require('factory').system().destroy();

        // remove log
        state.logs([]);

        // set default system
        if (systems.systems.length) {
          this.require('factory').create(this.require('storage').get(systems.systems[0]));
        }

        $('#designer-system-' + this.data().uuid).remove();
        this.require(this.data().ui).destroy();

        designer.require('state').space('');

        designer.require('spaces').render();
        designer.require('workspace').render();

        this.hide();

        if (isUninstalled) {
          this.require('message').success('The system has been deleted and the related extension has been uninstalled.');
        } else {
          this.require('message').success('The system has been deleted.');
        }
      });

      dialog.show();
    }.bind(this));
  }
}

