        result = that.require('factory').system().schemas()[id];
        break;
      }
    }
    return result;
  }

  function _getModel(name) {
    var result = '',
        id = '';

    for (id in that.require('factory').system().models()) {
      if (that.require('factory').system().models()[id]._name === name) {
        result = that.require('factory').system().models()[id];
        break;
      }
    }
    return result;
  }

  function _getModelFromComponent(id) {
    var result = '',
        modelName = '',
        componentId = '';

    for (modelName in that.require('factory').system().components()) {
      for (componentId in that.require('factory').system().components()[modelName]) {
        if (componentId === id) {
          result = modelName;
          break;
        }
      }
    }
    return result;
  }

  function _createLink(id) {
    var modelName = model[propName].type[0].replace('@', ''),
