  html = document.getElementById('designer-component-' + this.uuid().replace(/\./g, '-') + '-delete');
  if (html) {
    html.addEventListener('click', function click(event) {
      var Dialog = this.require('DialogDelete')
          data = {},
          dialog = {};

      dialog = new Dialog({
        'title': 'Confirm deletion',
        'message': 'Do you want to delete the component?'
      });

      data = dialog.data();

