function mergeBehavior(behaviors, behavior, sysId, systemName) { 
	var that = this,
	    source = {},
	    lastA = -1,
	    firstB = -1,
	    lastB = -1,
	    toAdd = '';

	function _getBehavior() {
	  var result = null,
	      id = '';

	  for (id in behaviors) {
	    if (behaviors[id].state === behavior.state && behaviors[id].component === sysId) {
	      result = behaviors[id];
	      break;
	    }
	  }

	  if (!result) {
	    result = {
	      "_id": that.generateId(),
	      "component": sysId,
	      "state": behavior.state,
	      "action": that.require('language').createBehavior(behavior.state),
	    };
	  }

	  return result;
	}  

	source = _getBehavior();

	lastA = source.action.lastIndexOf('}');
	firstB = behavior.action.indexOf('{');
	lastB = behavior.action.lastIndexOf('}');

	toAdd = behavior.action.substring(firstB + 1, lastB);

	if (toAdd.trim() !== '') { 
	  toAdd = this.require('language').createMergeComment(systemName) + toAdd + '\n}';
	} else {
	  toAdd = '}';
	}

	source.action = source.action.substring(0, lastA) + toAdd;

	behaviors[source._id] =  source;
}

