function createBehavior(type, model, state, def) { 
  var body = '\t\n',
      behaviors = this.system().behaviors();

  function _canCreate(type, component, state, behaviors) {
    var behavior = {},
        id = '',
        exist = false,
        result = true;

    for (id in behaviors) {
      behavior = behaviors[id];
      if (behavior.component === component && behavior.state === state) {
        exist = true;
        break;
      }
    }
    if (exist) {
      result = false;
    }

    return result;
  }

  if (_canCreate(type, model, state, behaviors)) {
    uuid = this.generateId();

    // params
    methodDef = def.params;
    params = this.require('language').createBehaviorParameters(methodDef);

    // body
    result = def.result;
    if (result) {
      body = this.require('language').createBehaviorBody(result);
    }

    // set behavior
    behavior = {
      "_id": uuid,
      "component": model,
      "state": state,
      "action": this.require('language').createBehavior(state, params, body),
      "useCoreAPI": false,
      "core": false
    };

    behaviors[uuid] = behavior;

    this.system().behaviors(behaviors);

    this.require('channel').$designerCreateBehavior(behavior);
  }
}

