function ok() { 
	var name = $('#modal-modeljson-name').val(),
	    jsonText = $('#modal-modeljson-json').val(),
	    json = null,
	    schema = {},
	    model = {},
	    property = '',
	    type = '',
	    typeName = '',
	    arrayType = '',
	    that = this,
	    createdTypes = [];

	function _getTypeName(parent, name) {
	  var result = name;

	  if (!_validType(name)) {
	    if (parent) {
	      result = parent + '_' + name;
	    } else {
	      result = name + Math.floor(Math.random() * 11);
	    }
	  }

	  return result;
	}    

  function _getType(value) {
    var type = '';

    if (Array.isArray(value)) {
      type = 'array';
    } else {
      type = typeof value;
    }

    return type;
  }   

  function _validName(name) {
    var result = true,
      id = '';

    for (id in that.require('factory').system().schemas()) {
      if (that.require('factory').system().schemas()[id]._name === name) {
        result = false;
        break;
      }
    }

    if (that.require('factory').system().name() === name) {
      result = false;
    }

    return result;
  } 

  function _validType(name) {
    var result = true,
      id = '';

    for (id in that.require('factory').system().types()) {
      if (that.require('factory').system().types()[id].name === name) {
        result = false;
        break;
      }
    }

    if (createdTypes.indexOf(name) !== -1) {
      result = false;
    }

    return result;
  } 

  function _createTypeFromObject(parent, name, obj) {
    var typeDef = '',
        property = '',
        type = '',
        typeName = '',
        arrayType = '';    

    typeName = _getTypeName(parent, name);
    typeDef = that.require('factory').addType(typeName, false);   

    typeDef.schema = {};
    that.require('designer').save();

    for (property in obj) {
      //if (property.indexOf('_') !== 0) {
        type = _getType(obj[property]);
        switch (type) {
          case 'object':
            typeDef.schema[property] = {};
            typeDef.schema[property].type = _createTypeFromObject(name, property, obj[property]);
            typeDef.schema[property].mandatory = false;
            typeDef.schema[property].default = {};
            break;
          case 'number':
            typeDef.schema[property] = {};
            typeDef.schema[property].type = 'number';
            typeDef.schema[property].mandatory = false;
            typeDef.schema[property].default = 0;
            break;
          case 'boolean':
            typeDef.schema[property] = {};
            typeDef.schema[property].type = 'boolean';
            typeDef.schema[property].mandatory = false;
            typeDef.schema[property].default = false;
            break;
          case 'array':
            typeDef.schema[property] = {};
            typeDef.schema[property].mandatory = false;
            if (typeof obj[property][0] !== 'undefined') {
              arrayType = _getType(obj[property][0]);

              if (arrayType === 'object') {
                if (obj[property][0] !== null) {
  	              typeDef.schema[property].type = [_createTypeFromObject(name, property, obj[property][0])];
  	              typeDef.schema[property].default = [];
  	            } else {
  	              typeDef.schema[property].type = ['object'];
  	              typeDef.schema[property].default = [];
  	            }
              } else {
                typeDef.schema[property].type = [arrayType];
                typeDef.schema[property].default = [];
              }
            } else {
              typeDef.schema[property].type = ['any'];
              typeDef.schema[property].default = [];
	          }
            break;
          default:
            typeDef.schema[property] = {};
            typeDef.schema[property].type = 'string';
            typeDef.schema[property].mandatory = false;
            typeDef.schema[property].default = '';
            break;
        }
      //}
    }
    that.require('factory').updateType(typeName, typeDef);
    that.require('designer').save();

    return typeName;
  }

  // clean name
  name = name.replace(/\s|\t/gi, '_').replace(/[\!\"\#\$\%\&\'\(\)\*\+\,\.\/\:\;\<\=\>\?\@\[\\\]\^\`\{\|\}\~]/gi, '');

	if (name && _validName(name) && jsonText) {
	  try {
	    json = JSON.parse(jsonText);
	    if (json) {
	    // schema
	    schema = this.require('factory').addSchema(name.trim());

	    schema._inherit = [];
	    this.require('factory').updateSchema(schema._id, schema);

	    this.require('designer').save();

	    for (property in json) {
	      if (property.indexOf('_') !== 0) {
  	      type = _getType(json[property]);
  	      if (type === 'array') {
  	        schema[property] =  'collection';
  	      } else {
  	        schema[property] =  'property';
  	      }
	      }
	    }

	    this.require('factory').updateSchema(schema._id, schema);
	    this.require('designer').save();

	    // model
	    model = this.require('factory').createModel(schema);
	    this.require('designer').save();

	    for (property in schema) {
	      if (property.indexOf('_') !== 0) {
	        type = _getType(json[property]);
	        switch (type) {
	          case 'object':
	            if (json[property] !== null) {
	              model[property].type = _createTypeFromObject('', property, json[property]);
	              model[property].default = {};
	            } else {
	              model[property].type = 'object';
	              model[property].default = {};
	            }
	            break;
	          case 'number':
	            model[property].type = 'number';
	            model[property].default = 0;
	            break;
	          case 'boolean':
	            model[property].type = 'boolean';
	            model[property].default = false;
	            break;
	          case 'array':
	            if (typeof json[property][0] !== 'undefined') {
	              arrayType = _getType(json[property][0]);
	              if (arrayType === 'object') {
	                if (json[property][0] !== null) {
    	              model[property].type = [_createTypeFromObject('', property, json[property][0])];
    	              model[property].default = [];
    	            } else {
    	              model[property].type = ['object'];
    	              model[property].default = [];
    	            }
	              } else {
	                model[property].type = [arrayType];
	                model[property].default = [];
	              }
	            } else {
	              model[property].type = ['any'];
	              model[property].default = [];
	            }
	            break;
	         	case 'string':
	            model[property].type = 'string';
	            model[property].default = '';
	            break;
	          default:
	            break;
	        }
	      }
	    }

	    this.require('factory').updateModel(model._id, model);
	    this.require('designer').save();

      this.require('channel').$designerCreateSchema(name, schema);

      this.hide();

      this.require('spaces').render();
      this.require('workspace').render();

	    this.require('message').success('A model has been created for the JSON.');
	    }
	  } catch(e) {
	    this.hide();
	    this.require('message').warning('Can not create the model: the JSON is invalid.');
	  }
	}    
}

