t1fb0b150c810528.action:
function render() { 
  var html = '',
      dom = null,
      templateId = this.html().id(),
      id = templateId.replace('.html', '');

  // empty area
  $('#designer-dialog').empty();

  // render
  html = this.html();
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',
    html.source()
      .replace(/{{title}}/gi, this.title())
      .replace(/{{message}}/gi, this.message())
  );

  // modal events
  dom = document.getElementById(id + '-cancel');
  dom.addEventListener('click', function click(event) {
    this.cancel();
  }.bind(this));

  dom = document.getElementById(id + '-ok');
  dom.addEventListener('click', function click(event) {
    this.ok();
  }.bind(this));		

  // show modal
  $('#' + id).modal('show');	
}

