function render(elt) { 
  var eltStyle = document.createElement('style'),
      style = this.style(),
      template = this.template(),
      domNode = elt;

  // render style
  if (style) {
    eltStyle.innerHTML = style;
    document.head.appendChild(eltStyle);
  }

  // render html
  if (template) {
    if (domNode === null) {
      domNode = document.body;
    }

    domNode.insertAdjacentHTML('beforeend', template);
  }
}

